<?php
include 'connect.php';

if ($_SERVER['REQUEST_METHOD'] == "POST") {
    $name = $_POST['name'];
    $price = $_POST['price'];
    $qty = $_POST['qty'];

    if (isset($_GET['updateid'])) {
        $id = $_GET['updateid'];
        $sql = "UPDATE products SET name='$name', price='$price', qty='$qty' WHERE id='$id'";
        $result = $con->query($sql);

        if ($result === true) {
            echo "Updated successfully";
            header('location: index.php');
        } else {
            die(mysqli_error($con));
        }
    }
}
